// src/App.js

import React, { useState, useEffect } from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import "./App.css";

function App() {
  const [activeSection, setActiveSection] = useState("home");

  const handleScroll = () => {
    const sections = ["home", "our-aim", "about", "downloads", "reports"];
    const scrollPosition = window.scrollY + 100; // Adjust as needed

    for (let i = sections.length - 1; i >= 0; i--) {
      const section = document.getElementById(sections[i]);
      if (section && section.offsetTop <= scrollPosition) {
        setActiveSection(sections[i]);
        break;
      }
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="app">
      {/* Navbar */}
      <nav className="navbar">
        <ul className="nav-links">
          <li>
            <Link
              to="home"
              smooth={true}
              duration={500}
              className={activeSection === "home" ? "active" : ""}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="our-aim"
              smooth={true}
              duration={500}
              className={activeSection === "our-aim" ? "active" : ""}
            >
              Our Aim
            </Link>
          </li>
          <li>
            <Link
              to="about"
              smooth={true}
              duration={500}
              className={activeSection === "about" ? "active" : ""}
            >
              About
            </Link>
          </li>
          <li>
            <Link
              to="downloads"
              smooth={true}
              duration={500}
              className={activeSection === "downloads" ? "active" : ""}
            >
              Downloads
            </Link>
          </li>
          <li>
            <Link
              to="reports"
              smooth={true}
              duration={500}
              className={activeSection === "reports" ? "active" : ""}
            >
              Reports
            </Link>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main className="main-content">
        {/* Sections as before */}
        {/* ... */}
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2025 Offy.AI. All Rights Reserved.</p>
      </footer>
    </div>
  );
}

export default App;
